mapper.select:
SELECT boardno,name,subject,content,TO_CHAR(day,'YYYY-MM-DD') as dbday, hit
FROM board
WHERE boardno=#{boardno}

mapper.update:
UPDATE board SET
hit = hit+1
WHERE boardno=#{boardno}

UPDATE board SET
name=#{name}, subject=#{subject}, content=#{content}
WHERE boardno=#{boardno}

mapper.select:
SELECT COUNT(*)
FROM Reply
WHERE boardno=#{bno}

SELECT group_id, group_step, group_tab
FROM Reply
WHERE replyno=#{replyno}

mapper.update:
UPDATE Reply SET
group_step=group_step+1 
WHERE group_id=#{group_id} AND group_step>#{group_step}

	  UPDATE Reply SET
  	depth=depth+1
  	WHERE replyno=#{replyno}

UPDATE Reply SET
msg=#{msg}
WHERE replyno=#{replyno}

UPDATE Reply SET
msg=#{msg}
WHERE replyno=#{replyno}

UPDATE Reply SET
depth=depth-1
WHERE replyno=#{replyno}

UPDATE board SET
hit = hit+1
WHERE boardno=#{boardno}

