mapper.update:
UPDATE member
SET reservecount = #{count}
WHERE id=#{id}

mapper.select:
select CEIL(COUNT(*)/5.0)
from reserve

	WHERE checheif!=3 AND checheif!=10 AND cheifid=#{id}


	WHERE checheif=3 OR checheif=10 AND cheifid=#{id}


	WHERE checheif!=3 AND checheif!=10 AND userid=#{id}


	WHERE checheif=3 OR checheif=10 AND userid=#{id}
